			ELSEIF localErrString = 'noResults' OR localErrString = 'tooMany' THEN 
				SET errString=CONCAT('There were ',localErrString,' results on the article member');
				LEAVE jumpOut;
			ELSE 
				SET whereCols=CONCAT_WS(' AND ', whereCols, 'article=?');
