		SET selectVarList=CONCAT_WS(',', selectVarList, '@TBL_WAArenderBlog_categoryTiespkResultcatcher');

		SET vl=JSON_UNQUOTE(JSON_EXTRACT(jsn,'$.pk'));
		SET tempTp=JSON_TYPE(vl);
		IF tempTp='NULL' OR vl IS NULL OR vl='' THEN 
			SET whereCols=CONCAT_WS(' AND ', whereCols, '(pk = ? OR 1)');
			SET pkVarcatcher=vl;
		ELSEIF tempTp='BOOLEAN' THEN 
			SET whereCols=CONCAT_WS(' AND ', whereCols, 'pk=?');
			SET pkVarcatcher=(vl='true');
			SET foundAColumn=1;
		ELSEIF tempTp='DOUBLE' THEN
			SET whereCols=CONCAT_WS(' AND ', whereCols, 'CONCAT(pk)=CONCAT(?)');
			SET pkVarcatcher=vl;
			SET foundAColumn=1;
		ELSE 
			SET whereCols=CONCAT_WS(' AND ', whereCols, 'pk=?');
			SET pkVarcatcher=vl;
			SET foundAColumn=1;
		END IF;

IF foundAColumn = 0 THEN 
