/* Apply styles to the entire contact info container */
.contact-info {
    background-color: #f5f5f5; /* Light gray background color */
    padding: 20px; /* Add padding for spacing */
    border-radius: 10px; /* Rounded corners */
    box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1); /* Box shadow for depth */
    max-width: 400px; /* Limit the width of the container */
    margin: 0 auto; /* Center the container horizontally */
}

/* Style the phone number and email paragraphs */
.contact-info p {
    margin: 10px 0; /* Add margin for vertical spacing */
}

/* Style the strong tags (e.g., "Phone Number:" and "Email:") */
.contact-info strong {
    color: #333; /* Darken the strong text */
    font-weight: bold;
}

/* Style the buttons */
.contact-info button {
    background-color: #007bff; /* Blue button background color */
    color: #fff; /* White text color */
    border: none; /* Remove border */
    padding: 5px 10px; /* Add padding for button size */
    margin-right: 10px; /* Add spacing between buttons */
    border-radius: 5px; /* Rounded corners */
    cursor: pointer; /* Change cursor on hover */
    transition: background-color 0.3s; /* Smooth color transition on hover */

}

.contact-info button:hover {
    background-color: #0056b3; /* Darker blue on hover */
}

/* Style the links */
.contact-info a {
    text-decoration: none; /* Remove underline from links */
    color: #007bff; /* Blue link color */
    margin-right: 10px; /* Add spacing between links */
}

.contact-info a:hover {
    text-decoration: underline; /* Underline links on hover */
}

