			CALL rmf.TBL_WAArenderAdmin_members_RESOLVE(vl,localErrString);

			IF localErrString = 'empty' THEN 
				SET whereCols=CONCAT_WS(' AND ', whereCols, '(member = ? OR member IS NULL OR member IS NOT NULL)');
				SET memberVarcatcher=NULL;
			ELSEIF localErrString = 'noResults' OR localErrString = 'tooMany' THEN 
				SET errString=CONCAT('There were ',localErrString,' results on the member member');
				LEAVE jumpOut;
			ELSE 
				SET whereCols=CONCAT_WS(' AND ', whereCols, 'member=?');
				SET memberVarcatcher=JSON_UNQUOTE(JSON_EXTRACT(vl, '$.pk'));
				SET foundAColumn=1;
			END IF;

		ELSE 
			SET whereCols=CONCAT_WS(' AND ', whereCols, '(member = ? OR member IS NULL OR member IS NOT NULL)');
			SET memberVarcatcher=vl;
		END IF;

		SET selectColList=CONCAT_WS(',', selectColList, 'pk');
		SET selectVarList=CONCAT_WS(',', selectVarList, '@TBL_WAArenderAdmin_passwordRecoveryRequestspkResultcatcher');

		SET vl=JSON_UNQUOTE(JSON_EXTRACT(jsn,'$.pk'));
		SET tempTp=JSON_TYPE(vl);
		IF tempTp='NULL' OR vl IS NULL OR vl='' THEN 
			SET whereCols=CONCAT_WS(' AND ', whereCols, '(pk = ? OR 1)');
			SET pkVarcatcher=vl;
		ELSEIF tempTp='BOOLEAN' THEN 
			SET whereCols=CONCAT_WS(' AND ', whereCols, 'pk=?');
			SET pkVarcatcher=(vl='true');
			SET foundAColumn=1;
		ELSEIF tempTp='DOUBLE' THEN
			SET whereCols=CONCAT_WS(' AND ', whereCols, 'CONCAT(pk)=CONCAT(?)');
			SET pkVarcatcher=vl;
			SET foundAColumn=1;
		ELSE 
			SET whereCols=CONCAT_WS(' AND ', whereCols, 'pk=?');
			SET pkVarcatcher=vl;
			SET foundAColumn=1;
		END IF;

		SET selectColList=CONCAT_WS(',', selectColList, 'timeAdded');
		SET selectVarList=CONCAT_WS(',', selectVarList, '@TBL_WAArenderAdmin_passwordRecoveryRequeststimeAddedResultcatcher');

		SET vl=JSON_UNQUOTE(JSON_EXTRACT(jsn,'$.timeAdded'));
