		ELSEIF tempTp='BOOLEAN' THEN 
			SET whereCols=CONCAT_WS(' AND ', whereCols, 'timeAdded=?');
			SET timeAddedVarcatcher=(vl='true');
			SET foundAColumn=1;
		ELSEIF tempTp='DOUBLE' THEN
			SET whereCols=CONCAT_WS(' AND ', whereCols, 'CONCAT(timeAdded)=CONCAT(?)');
			SET timeAddedVarcatcher=vl;
			SET foundAColumn=1;
		ELSE 
			SET whereCols=CONCAT_WS(' AND ', whereCols, 'timeAdded=?');
			SET timeAddedVarcatcher=vl;
			SET foundAColumn=1;
		END IF;

		SET selectColList=CONCAT_WS(',', selectColList, 'token');
		SET selectVarList=CONCAT_WS(',', selectVarList, '@TBL_WAArenderAdmin_passwordRecoveryRequeststokenResultcatcher');

		SET vl=JSON_UNQUOTE(JSON_EXTRACT(jsn,'$.token'));
