		IF tempTp='NULL' OR vl IS NULL OR vl='' THEN 
			SET whereCols=CONCAT_WS(' AND ', whereCols, '(token = ? OR 1)');
			SET tokenVarcatcher=vl;
		ELSEIF tempTp='BOOLEAN' THEN 
