		ELSEIF tempTp='DOUBLE' THEN
			SET whereCols=CONCAT_WS(' AND ', whereCols, 'CONCAT(token)=CONCAT(?)');
			SET tokenVarcatcher=vl;
			SET foundAColumn=1;
		ELSE 
			SET whereCols=CONCAT_WS(' AND ', whereCols, 'token=?');
			SET tokenVarcatcher=vl;
			SET foundAColumn=1;
		END IF;

IF foundAColumn = 0 THEN 
